func trailingZeros8(x uint8) int {
	return int(ntz8tab[x])
}

type GolombBitWriter struct {
	w    io.Writer
	buf  [1]byte
	left byte
}

func NewGolombBitWriter(w io.Writer) *GolombBitWriter {
	return &GolombBitWriter{
		w:    w,
		left: 8,
	}
}

func (self *GolombBitWriter) WriteBit(v uint) error {
	self.left--
	self.buf[0] |= byte((v & 0x01) << self.left)
	if self.left == 0 {
		if _, err := self.w.Write(self.buf[:]); err != nil {
			return err
		}
		self.buf[0] = 0
		self.left = 8
	}
	return nil
}

func (self *GolombBitWriter) WriteBits(v uint, n int) error {
	for i := 0; i < n; i++ {
		if err := self.WriteBit((v >> uint(n-i-1)) & 0x01); err != nil {
			return err
		}
	}
	return nil
}

func (self *GolombBitWriter) IsByteAligned() bool {
	return self.left == 8
}

func (self *GolombBitWriter) WriteTrailingBits() error {
	for !self.IsByteAligned() {
		if err := self.WriteBit(0); err != nil {
			return err
		}
	}
	return nil
}
